            {word}


        for (var i=0; i < 3; i++){
          {this.state.listOfWords[i].name key={i}}
        }
          </a>
        ))} */}
        {/* //     <ul>
    //     <li>
    // {this.state.map(function(word, i){
    //     return {this.state.listOfWords[i].name key={i}};
    // })}
    //   </li>
    //   </ul> */}
      </div>
    );
  }
}

export default VocabList;
