import React, { Component } from "react";
import storageService from "../../services/storageService";
import { getWordId } from "../../services/API";

export class MyDocuments extends Component {
  state = {
    listOfDocs: [],
    paragraph: [],
    textparagraph: "",
  };

  componentDidMount() {
    console.log("Doc List component mounted");
    let token = localStorage.getItem("token");
    return fetch("/api/users/docList", {
      method: "POST",
      headers: new Headers({
        "Content-Type": "application/json",
        Authorization: token,
        body: JSON.stringify({ hello: "hello" }),
      }),
    })
      .then(async (res) => {
        console.log("we got a result! " + res);
        if (res.ok) {
          try {
            let listOfDocs = await res.json();
            console.log(listOfDocs);
            this.setState({
              listOfDocs: listOfDocs,
            });
            return listOfDocs;
          } catch (err) {
            console.log("this is the catch err" + err);
          }
          throw new Error("Email already taken!");
        }
      })
      .then(() => console.log(this.state.listOfDocs[0].name))
      .catch((error) => console.log(error));
  }

  handleDef = async (e) => {
    e.preventDefault();
    console.log(e.target.name.value);
    const name = e.target.name.value;
    console.log(e.target.paragraph_text.value);
    this.setState({
      textparagraph: e.target.paragraph_text.value,
    });
    const paragraph = e.target.paragraph_text.value;
    const myParagraph = `${paragraph}`;
    // splits up submitted text
    const splitString = myParagraph.split(" ");
    for (let i = 0; i < splitString.length - 1; i++) {
      splitString[i] += " ";
    }
    console.log("this is the splitString: " + splitString);
    this.setState({
      paragraph: splitString,
      docName: name,
    });
    console.log("HandleDef Ran");
    console.log(e.target.text);
    const search = e.target.text;
    let WordId = await getWordId(search);
    console.log(WordId[0].meta.id);
    console.log(WordId[0].shortdef);

    const id = WordId[0].meta.id;
    const definition = WordId[0].shortdef;

    let stringId = id.toString();
    var newId;
    var colon;
    if (stringId.includes(":")) {
      colon = id.indexOf(":");
      newId = stringId.slice(0, colon);
      this.setState({
        id: newId,
        definition: definition,
      });
    } else {
      console.log("word doesnt have a colon");
      this.setState({
        id: id,
        definition: definition,
      });
    }
  };

  showDef = (e) => {
    e.preventDefault();
    console.log(e.target.text);
  };

  handleDelete = (e) => {
    console.log("I ran handleDelete");
    console.log(e.target.value);
    var matchedName;
    let listOfDocs = this.state.listOfDocs;
    for (let i = 0; i < this.state.listOfDocs.length; i++) {
      if (e.target.value === listOfDocs[i].name) {
        console.log("We matched! " + listOfDocs[i].name);
        console.log("We matched! " + listOfDocs[i].paragraph);
        matchedName = {
          id: listOfDocs[i].name,
          paragraph: listOfDocs[i].paragraph,
        };
      }
    }
    const savedWord = matchedName;
    storageService.deleteDoc(savedWord);
    this.componentDidMount();
  };

  render() {
    return (
      <div>
        <span className="title">Documents</span>
        <p>Tada here are your list of Docs</p>
        {/* {this.state.listOfDocs[0].name} */}

        <ul>
          {this.state.listOfDocs.map((word) => (
            <li>
              <a href="" onClick={this.showDef} className="word">
                {word.name}
                <br />
              </a>
              <p>
                {this.state.listOfDocs.map((word) => (
                  <a href="/document" onClick={this.handleDef} className="word">
                    {word.paragraph}
                  </a>
                ))}
              </p>
              <br />
              <button
                type="Submit"
                value={word.name}
                onClick={this.handleDelete}
                className="submit-save"
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}

export default MyDocuments;
